        private DirectoryInfo gameDir;
        private List<Item> items = new List<Item>();

        public ItemScanner(DirectoryInfo gameDir)
        {
            this.gameDir = gameDir;
        }


        public Task init()
        {
            return Task.Run(async () =>
            {
                items = await scan(gameDir);
            });
        }

        public static Task<List<Item>> scan(DirectoryInfo gameDir)
        {
            return Task.Run(() =>
            {
                List<Item> items = new List<Item>();

                DirectoryInfo bundles = new DirectoryInfo(gameDir.FullName + "\\Bundles\\Items\\");
                if (bundles.Exists)
                {
                    items.AddRange(scanDir(gameDir));
                }
                else
                {
                    return Fallback;
                }

                return items;
            });
        }

        private static IEnumerable<Item> scanDir(DirectoryInfo dir)
        {
          
            if (Item.isValidItemDir(dir))
            {
                Item i = Item.loadFromDir(dir);
                yield return i;
            }

            foreach (DirectoryInfo subdir in dir.GetDirectories())
            {
                foreach(Item i in scanDir(subdir))
                {
                    yield return i;
                }
            }
            
        }

        public Item getItemByID(string id)
        {

            foreach (Item item in items)
            {
                if (item.ID == id)
                {
                    return item;
                }
            }
            return null;
        }

        public Item getItembyName(string name)
        {

            foreach (Item item in items)
            {
                if (item.Name == name)
                {
                    return item;
                }
            }
            return null;
        }

        public List<Item> getItems()
        {
            return items.Count == 0 ? Fallback : items;
        }


    }

